plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    application
    id("com.gradleup.shadow") version "8.3.4"
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    // Picocli for command line argument parsing
    implementation("info.picocli:picocli:4.7.5")
    annotationProcessor("info.picocli:picocli-codegen:4.7.5")
    
    // JSON support
    implementation("com.fasterxml.jackson.core:jackson-databind:2.16.1")
    implementation("com.fasterxml.jackson.dataformat:jackson-dataformat-yaml:2.16.1")
    
    // Logging
    implementation("org.slf4j:slf4j-api:2.0.11")
    implementation("ch.qos.logback:logback-classic:1.4.14")

    // Testing
    testImplementation("org.junit.jupiter:junit-jupiter:5.12.1")
    testImplementation("org.bouncycastle:bcprov-jdk18on:1.77")
    testImplementation("org.bouncycastle:bcpkix-jdk18on:1.77")
}

testing {
    suites {
        // Configure the built-in test suite
        val test by getting(JvmTestSuite::class) {
            // Use JUnit Jupiter test framework
            useJUnitJupiter("5.12.1")
        }
    }
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

// application {
//     // Define the main class for the application.
//     mainClass = "org.example.App"
// }

application {
    // this makes `./gradlew run` pick up your SSLTest.main
    mainClass.set("org.example.SSLTest")
}

// Optional: configure the built-in `run` task with default args
tasks.named<JavaExec>("run") {
    // Enable command line argument passing
    standardInput = System.`in`
}
